import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Auth from './AuthService';
import AddNewProject from './AddNewProject';

export default class StudentsDetails extends Component {
    constructor(props) {
        super(props);
        this.state = {
          student: null,
          projects: []
        };
      }

    componentDidMount () {
        console.log('componentDidMount')
        Auth.fetch (`/students/${this.props.student_id}`, {
            method: 'GET',
        })
          .then(response => {
            console.log(response);
            this.setState(() => ({ student: response }));
       })
       .catch(error => {
         console.error('Server Error', error);
       }); 
       Auth.fetch (`/students-projects/${this.props.student_id}`, {
        method: 'GET',
    })
      .then(response => {
        console.log(response);
        this.setState(() => ({ projects: response }));
   })
   .catch(error => {
     console.error('Server Error', error);
   }); 
    }

    postProject = (project) => { 
        console.log('postProject', project);
        Auth.fetch (`/projects`, {
          method: 'POST',
          body: JSON.stringify(project)
      })
        .then (response => {
            return Auth.fetch(`/projects/${response.id}`,{
                method: 'GET'
            })
        })
        .then(response => {
          console.log(response);
          this.setState({
                 projects: this.state.projects.concat(response)
          })
        })
        .catch(error => {
            console.error('Server Error', error);
          }); 
    }

    render() {
        const student = this.state.student
       if (student == null) 
           return null;
      return (
          <div className="second">
        <div className="Student">
         <h3>Student name {student.firstname} {student.lastname}</h3>
         <p>Email {student.email}</p>
         <p>Projects {(this.state.projects ||[]).map(project => (
                     <div key={project.id}>
                             {project.project_name}
                     </div>)) 
            }
        </p>
        <Link  className="home-button" to="/">Home</Link>
        <AddNewProject
        postProject={this.postProject}/>
        </div>
        </div>
      )   
    }
}
